import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root') // This line is needed to hide the rest of the app when the modal is open

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.5)',
                    zIndex: 1000
                },
                content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80%',
                    height: '80%',
                    display: 'flex',
                    justifyContent: 'center',
                    alignItems: 'center',
                    flexDirection: 'column',
                    border: 'none',
                    borderRadius: '4px',
                    padding: '20px',
                    color: 'white',
                    backgroundColor: 'rgba(0,0,0,0.8)'
                }
            }}
        >
            <button
                style={{
                    background: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    padding: '10px 20px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    position: 'absolute',
                    top: '10px',
                    right: '10px'
                }}
                onClick={closeModal}
            >
                Close
            </button>
            <div style={{ display: 'flex', width: '100%', height: '100%' }}>
                <img src={project.image} alt={project.title} style={{ width: '50%', height: 'auto' }} />
                <div style={{ width: '50%', padding: '20px' }}>
                    <h2>{project.title}</h2>
                    <p>{project.description}</p>
                </div>
            </div>
        </Modal>
    );
}

export default ProjectModal;
