import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root') // This line is needed to hide the rest of the app when the modal is open

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            className="box-shadow-full"
            overlayClassName={{
                base: 'project-modal-overlay',
                afterOpen: 'project-modal-overlay_after-open',
                beforeClose: 'project-modal-overlay_before-close'
            }}
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.5)',
                    zIndex: 1000
                },
                content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80%',
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'row',
                    border: 'none',
                    borderRadius: '4px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4'
                }
            }}
        >
            <button className="btn btn-primary btn js-scroll px-4" style={{ position: 'absolute', top: '10px', right: '10px' }} onClick={closeModal}>
                Close
            </button>
            <div className="row">
                <div className="col-sm-6">
                    <img src={project.image} alt={project.title} style={{ width: '100%', height: 'auto' }} />
                </div>
                <div className="col-sm-6 pt-4 pt-md-0">
                    <div className="title-box-2">
                        <h5 className="title-left">{project.title}</h5>
                    </div>
                    <p className="lead">{project.description}</p>
                </div>
            </div>
        </Modal>
    );
}

export default ProjectModal;
