import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root') // This line is needed to hide the rest of the app when the modal is open

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80%',
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'row',
                    border: 'none',
                    borderRadius: '10px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    overflow: 'auto',
                }
            }}
        >

            <div className="row">
                <div className="col-md-12">
                    <div className="about-me pt-4 pt-md-0">
                        <div className="title-box">
                            <img src={project.image} alt={project.title} style={{
                                width: '80%',
                                height: '80%',
                            }}/>
                        </div>
                        <div className="title-box-2">
                            <h5>{project.title}</h5>
                            <p className="lead">{project.description}</p>
                            <button
                                style={{
                                    background: '#007bff',
                                    color: 'white',
                                    border: 'none',
                                    borderRadius: '4px',
                                    padding: '10px 20px',
                                    fontSize: '1rem',
                                    cursor: 'pointer',
                                    position: 'absolute',
                                    top: '10px',
                                    right: '10px'
                                }}
                                onClick={closeModal}
                            >
                                Close
                            </button>
                        </div>
                    </div>
                </div>
            </div>

        </Modal>
    );
}

export default ProjectModal;
