import React from "react";
import ProjectModal from './ProjectModal';

// import images
import stock from "../img/image1.jpg";
import stock1 from "../img/image2.jpg";
import stock2 from "../img/image3.jpg";
import stock3 from "../img/image4.jpg";
import stock4 from "../img/image5.jpg";
import stock5 from "../img/image6.jpg";

class Portfolio extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showModal: false,
            selectedProject: null
        };
        this.projects = [
            {
                image: stock,
                title: 'Snowcast Showdown Kaggle Challenge',
                description: 'Python Tensorflow Pandas Numpy Librosa Sklearn Matplotlib',
                githubLink: 'https://github.com/flippi247/Snowcast-Showdown-Challenge'
            },
            {
                image: stock1,
                title: 'Speech Emotion Recognition Models',
                description: 'Python Pytorch Pandas Numpy Librosa Sklearn Matplotlib',
                githubLink: 'https://github.com/flippi247/Speech-Emotion-Recognition'
            },
            {
                image: stock2,
                title: 'Cancer Cell Segmentation Challenge',
                description: 'Python Pytorch Pandas Numpy Matplotlib',
                githubLink: 'https://github.com/flippi247/Cancer-Cell-Segmentation-Challenge'
            },
            {
                image: stock3,
                title: 'Adaptive HMI Concept',
                description: 'Java Apache-Tinkerpop Gremlin Janusgraph',
                githubLink: 'https://github.com/flippi247/DroneZoneRepo'
            }
        ];
    }

    handleImageClick = (project) => {
        this.setState({
            selectedProject: project,
            showModal: true
        });
    }

    closeModal = () => {
        this.setState({
            showModal: false,
            selectedProject: null
        });
    }

    render() {
        return (
            <section id="work" className="portfolio-mf sect-pt4 route">
                <div className="container">
                    <div className="row">
                        <div className="col-sm-12">
                            <div className="title-box text-center">
                                <h3 className="title-a">Portfolio</h3>
                                <p className="subtitle-a">
                                    Explore my projects and see how I use technology to solve problems.
                                </p>
                                <div className="line-mf"></div>
                            </div>
                        </div>
                    </div>
                    <div className="row">
                        {this.projects.map((project, index) => (
                            <div key={index} className="col-md-4">
                                <div className="work-box">
                                    <div className="work-img">
                                        <img
                                            src={project.image}
                                            alt=""
                                            className="img-fluid"
                                            style={{
                                                width: '100%',
                                                height: '300px',
                                                objectFit: 'cover',
                                                objectPosition: 'center'
                                            }}
                                            onClick={() => this.handleImageClick(project)}
                                        />
                                    </div>
                                    <div className="work-content">
                                        <div className="row">
                                            <div className="col-sm-8">
                                                <h2 className="w-title">{project.title}</h2>
                                                <div className="w-more">
                                                    <span className="w-ctegory">{project.description}</span>
                                                </div>
                                            </div>
                                            <div className="col-sm-4">
                                                <div className="w-like" onClick={() => window.open(project.githubLink, "_blank")}>
                                                    <span className="ion-social-github"></span>
                                                </div>
                                            </div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
                {this.state.selectedProject &&
                    <ProjectModal
                        isOpen={this.state.showModal}
                        closeModal={this.closeModal}
                        project={this.state.selectedProject}
                    />
                }
            </section>
        );
    }
}

export default Portfolio;
