import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root')

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    top: '10%',
                    left: '10%',
                    right: '10%',
                    bottom: '10%',
                    border: 'none',
                    borderRadius: '10px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    display: 'flex',
                    flexDirection: 'row',
                    overflow: 'hidden',
                }
            }}
        >
            <div style={{width: '50%', height: '80vh'}}>
                <img src={project.image} alt={project.title} style={{width: '100%', height: 'auto', objectFit: 'cover'}} />
            </div>
            <div style={{width: '50%', marginLeft: '2%', height: '80vh', display: 'flex', flexDirection: 'column', justifyContent: 'space-between', overflowY: 'scroll'}}>
                <div>
                    <h5 className="title-left" style={{ marginBottom: '5%' }}>{project.title}</h5>
                    <p className="lead" dangerouslySetInnerHTML={{ __html: project.description.replace(/\n/g, '<br />') }} />
                </div>
                <button
                    style={{
                        background: '#007bff',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        padding: '10px 20px',
                        fontSize: '1rem',
                        cursor: 'pointer',
                        maxWidth: '200px',
                        margin: 'auto'
                    }}
                    onClick={closeModal}
                >
                    Close
                </button>
            </div>
        </Modal>
    );
}

export default ProjectModal;
