import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root')

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    position: 'absolute',
                    top: '10%',
                    left: '10%',
                    right: '10%',
                    bottom: '10%',
                    border: 'none',
                    borderRadius: '10px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    overflow: 'auto',
                    display: 'flex',
                    flexDirection: 'column-reverse',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                }
            }}
        >
            <div style={{width: '100%', display: 'flex', flexDirection: 'row-reverse', justifyContent: 'space-between'}}>
                <div style={{flex: 1, paddingRight: '20px'}}>
                    <h5 className="title-left">{project.title}</h5>
                    <p className="lead" dangerouslySetInnerHTML={{ __html: project.description.replace(/\n/g, '<br />') }} />
                </div>
                <img src={project.image} alt={project.title} style={{flex: 1, maxHeight: '80vh', objectFit: 'contain'}} />
            </div>
            <button
                style={{
                    background: '#007bff',
                    color: 'white',
                    border: 'none',
                    borderRadius: '4px',
                    padding: '10px 20px',
                    fontSize: '1rem',
                    cursor: 'pointer',
                    maxWidth: '200px',
                    alignSelf: 'center'
                }}
                onClick={closeModal}
            >
                Close
            </button>
        </Modal>
    );
}

export default ProjectModal;
