import React from 'react';
import Modal from 'react-modal';
import '../modal.css';

Modal.setAppElement('#root')

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            className="ProjectModalContainer"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    border: 'none',
                    borderRadius: '10px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    padding: '0', // Kein Padding im Modal selbst
                    overflow: 'auto',
                    width: '80%', // Breite und Höhe können auch angepasst werden, um das gewünschte Verhalten zu erreichen.
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'column', // Flex Direction Column für das äußere Element
                    justifyContent: 'center',
                    alignItems: 'center',
                }
            }}
        >
            <div className="ProjectModalContent" style={{width: '100%', display: 'flex', flexDirection: 'row', padding: '20px'}}>
                <div style={{width: '50%', padding: '2%'}}>
                    <img src={project.image} alt={project.title} style={{width: '100%', height: 'auto'}} />
                </div>
                <div style={{width: '50%', marginLeft: '2%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between'}}>
                    <div>
                        <h5 className="title-left" style={{ marginBottom: '5%' }}>{project.title}</h5>
                        <p className="lead" dangerouslySetInnerHTML={{ __html: project.description.replace(/\n/g, '<br />') }} />
                    </div>
                    <button
                        style={{
                            background: '#007bff',
                            color: 'white',
                            border: 'none',
                            borderRadius: '4px',
                            padding: '10px 20px',
                            fontSize: '1rem',
                            cursor: 'pointer',
                            maxWidth: '200px',
                            margin: 'auto'
                        }}
                        onClick={closeModal}
                    >
                        Close
                    </button>
                </div>
            </div>
        </Modal>
    );
}

export default ProjectModal;
