import React from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root') // Ein wichtiges Detail, es ermöglicht Modal, den Hintergrund auszublenden, wenn das Modal geöffnet ist.

function ProjectModal({ isOpen, closeModal, project }) {
    const customStyles = {
        overlay: {
            backgroundColor: 'rgba(0, 0, 0, 0.75)',
            zIndex: 1000,
        },
        content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            marginRight: '-50%',
            transform: 'translate(-50%, -50%)',
            color: 'black',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center'
        },
    };

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            style={customStyles}
            contentLabel="Project Modal"
        >
            <div className="project-modal">
                <img src={project.image} alt="" className="img-fluid" />
                <div className="project-details">
                    <h2 className="w-title">{project.title}</h2>
                    <div className="w-more">
                        <span className="w-ctegory">{project.description}</span>
                    </div>
                    <p className="pt-3">
                        <button
                            className="btn btn-primary btn js-scroll px-4"
                            onClick={closeModal}
                        >
                            Close
                        </button>
                    </p>
                </div>
            </div>
        </Modal>
    );
}

export default ProjectModal;
