import React from 'react';
import Modal from 'react-modal';
import '../../public/css/modal.css;'  // import the CSS file

Modal.setAppElement('#root')

function ProjectModal({ isOpen, closeModal, project }) {
    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    top: '50%',
                    left: '50%',
                    right: 'auto',
                    bottom: 'auto',
                    marginRight: '-50%',
                    transform: 'translate(-50%, -50%)',
                    width: '80%',
                    height: '80%',
                    display: 'flex',
                    flexDirection: 'row',
                    border: 'none',
                    borderRadius: '10px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    overflow: 'auto',
                }
            }}
        >
            <div style={{width: '50%', flexShrink: 0}}>
                <img src={project.image} alt={project.title} style={{width: '100%', height: 'auto'}} />
            </div>
            <div style={{width: '50%', marginLeft: '2%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between'}}>
                <div>
                    <h5 style={{marginBottom: '1rem'}}>{project.title}</h5>
                    <p dangerouslySetInnerHTML={{ __html: project.description.replace(/\n/g, '<br />') }} />
                </div>
                <button
                    style={{
                        background: '#007bff',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        padding: '10px 20px',
                        fontSize: '1rem',
                        cursor: 'pointer',
                        maxWidth: '200px',
                        alignSelf: 'center'
                    }}
                    onClick={closeModal}
                >
                    Close
                </button>
            </div>
        </Modal>

    );
}

export default ProjectModal;
