import React, { useEffect } from 'react';
import Modal from 'react-modal';

Modal.setAppElement('#root')

function ProjectModal({ isOpen, closeModal, project }) {

    const isSmallScreen = window.innerWidth < 700;

    useEffect(() => {
        if (isOpen) {
            document.body.style.overflow = 'hidden';
        } else {
            document.body.style.overflow = 'unset';
        }
    }, [isOpen]);

    return (
        <Modal
            isOpen={isOpen}
            onRequestClose={closeModal}
            contentLabel="Project Modal"
            style={{
                overlay: {
                    backgroundColor: 'rgba(0,0,0,0.7)',
                    zIndex: 100000000
                },
                content: {
                    top: isSmallScreen ? '5%' : '10%',
                    left: isSmallScreen ? '5%' : '10%',
                    right: isSmallScreen ? '5%' : '10%',
                    bottom: isSmallScreen ? '5%' : '10%',
                    border: 'none',
                    borderRadius: '10px',
                    padding: '20px',
                    color: '#333',
                    backgroundColor: '#f4f4f4',
                    display: 'flex',
                    flexDirection: isSmallScreen ? 'column' : 'row',
                    alignItems: isSmallScreen ? 'center' : 'stretch',
                    overflow: 'hidden',
                }
            }}
        >
            <div style={{width: '100%', height: isSmallScreen ? '30vh' : '80vh', display: 'flex', justifyContent: 'center', alignItems: 'center'}}>
                <img src={project.image} alt={project.title} style={{maxWidth: '100%', maxHeight: '100%'}} />
            </div>
            <div style={{width: '100%', marginLeft: isSmallScreen ? 0 : '2%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between', overflowY: 'auto'}}>
                <div style={{height: isSmallScreen ? '50vh' : '80vh', overflowY: 'auto'}}>
                    <h5 className="title-left" style={{ marginBottom: '5%', fontSize: isSmallScreen ? '0.9em' : '1em' }}>{project.title}</h5>
                    <p className="lead" dangerouslySetInnerHTML={{ __html: project.description.replace(/\n/g, '<br />') }} style={{fontSize: isSmallScreen ? '0.8em' : '1em'}}/>
                </div>
                <button
                    style={{
                        background: '#007bff',
                        color: 'white',
                        border: 'none',
                        borderRadius: '4px',
                        padding: '10px 20px',
                        fontSize: isSmallScreen ? '0.8rem' : '1rem',
                        cursor: 'pointer',
                        maxWidth: '200px',
                        margin: 'auto'
                    }}
                    onClick={closeModal}
                >
                    Close
                </button>
            </div>
        </Modal>
    );
}

export default ProjectModal;
